select
distinct
from core.location l Where l.id in ( WITH RECURSIVE locations AS ( SELECT lm1.geojson_id, lm1.parent_id, lm1.location_id FROM core.location_metadata lm1 WHERE lm1.geojson_id IN
#{value}
AND lm1.status IN ('ACTIVE', 'PENDING_REVIEW') UNION SELECT lm2.geojson_id, lm2.parent_id, lm2.location_id FROM locations lm1 INNER JOIN core.location_metadata lm2 on lm2.parent_id = lm1.geojson_id AND status IN ('ACTIVE', 'PENDING_REVIEW') ) SELECT location_id FROM locations )
order by lm.${example.orderByClause}
LIMIT #{limit} OFFSET #{offset}

