select
distinct
from core.location l Where l.id in ( WITH RECURSIVE locations AS ( SELECT lm1.geojson_id, lm1.parent_id, lm1.location_id FROM core.location_metadata lm1 WHERE geojson_id = #{locationId,jdbcType=VARCHAR} UNION SELECT lm2.geojson_id, lm2.parent_id, lm2.location_id FROM locations lm1 INNER JOIN core.location_metadata lm2 on lm2.parent_id = lm1.geojson_id ) SELECT location_id FROM locations )
order by lm.${example.orderByClause}
LIMIT #{limit} OFFSET #{offset}

