sql.if:
with locationAndTag as( select lm.id, lm.location_id, lm.parent_id, lm."name", ltm.location_tag_id, lt."name" tag_name from core.location_metadata as lm join core.location_tag_map as ltm on lm.location_id = ltm.location_id left join core.location_tag lt on lt.id = ltm.location_tag_id
lm.name ilike '%${name}%'
and ltm.location_tag_id = #{locationTagId}
and lm.parent_id::bigint = #{parentId}
and lm.status = #{status}
), parent_location as ( select lt.parent_id, lt.location_id, lt.id, lt."name", lm."name" parent, lt.tag_name from locationAndTag lt left join core.location_metadata lm on lt.parent_id::bigint = lm.location_id )
