mapper.update:

	SELECT seq.*,jsonb_set(#{json,jdbcType=OTHER,typeHandler=org.opensrp.repository.postgres.handler.TaskTypeHandler},'{serverVersion}',to_jsonb(server_version),true) as json
	FROM (SELECT nextVal('core.task_server_version_seq') as server_version) seq

   update core.task
   set json = #{json,jdbcType=VARCHAR},
     server_version = #{serverVersion,jdbcType=BIGINT}
   where id = #{id,jdbcType=BIGINT}
